def get_sample(script_type, script_subtype = None):
    """
    :return: a 2-tuple of (outscript, inscript)
    """
    d = get_sample_data(script_type, script_subtype)
    o = outscript_from_raw(bytes.fromhex(d['outscript']))
    i = inscript_from_raw(bytes.fromhex(d['inscript']), o)
    return o, i

def get_sample_data(script_type, script_subtype = None):
    """
    :return: sample data, as a dict, with keys: inscript, outscript, txid, iidx, oidx, etc.
    """
    script_type = getattr(script_type, 'name', script_type)
    script_subtype = getattr(script_subtype, 'name', script_subtype)
    script_type = ScriptType[script_type]
    if script_type == ScriptType.P2SH:
        if script_subtype is None:
            script_subtype = ScriptType.P2MULTISIG
        else:
            script_subtype = ScriptType[script_subtype]
        return P2SH_SCRIPT_SAMPLES[script_subtype.name]
    else:
        return SCRIPT_SAMPLES[script_type.name]

def get_sample_exec_args(script_type, script_subtype = None, flags = ()):
    # extract data from sample:
    d = get_sample_data(script_type, script_subtype)
    #d['inscript'] = d['inscript'][:-2] + 'ff'  # HACK ERROR PLANTING (useful for debugging the debugger)
    #d['inscript'] = d['inscript'][2:]  # HACK ERROR PLANTING (useful for debugging the debugger)
    #d['txblob'] = '99' + d['txblob'][2:]  # HACK ERROR PLANTING (useful for debugging the debugger)
    outscript = outscript_from_raw(bytes.fromhex(d['outscript']))
    inscript = inscript_from_raw(bytes.fromhex(d['inscript']), outscript)
    rawtx = bytes.fromhex(d['txblob'])
    input_idx = d['iidx']
    
    # set some flags
    if outscript.type == ScriptType.P2SH and SCRIPT_VERIFY_P2SH not in flags:
        flags += ( SCRIPT_VERIFY_P2SH, )
        
    return (inscript, outscript), dict(flags = flags, rawtx = rawtx, input_idx = input_idx)

